    private void ordenarNomeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ordenarNomeActionPerformed
        // TODO add your handling code here:
        BubbleSort.ordenaBolha(principal.vetorDeEmpregados,1);
        for(int i=0; i<principal.vetorDeEmpregados.size();i++){
            Empregado e =(Empregado) principal.vetorDeEmpregados.get(i);
            tela.setText(tela.getText()+ "\n" + e.getMatricula() + " " + e.getNome() + " " + e.getSalario());
        }
        
    }//GEN-LAST:event_ordenarNomeActionPerformed

